  //     ]
  //   },
  //   {
  //     id: 445,
  //     title: "Complete MEAN project",
  //     description: "MEAN motivational project",
  //     totalTime: 5,
  //     completed: true,
  //     logs: [
  //       { Description: "3 hour at home", duration: 1 },
  //       { Description: "2 hours at home", duration: 2 }
  //     ]
  //   }
  // ]);
  const [enteredGoal, setEnteredGoal] = useState([]);
  const deleteGoalHandler = goal => {
    console.log(goal);
    // setEnteredGoal(prevGoals => {
    //   prevGoals.splice(goal);
    // })
  };
  const addGoalHandler = (goal, id) => {
    setEnteredGoal(prevGoals => [
      ...prevGoals,
      { id: Math.random().toString(), ...goal }
    ]);
  };

  console.log(enteredGoal);
  return (
    <Router>
      <div className="App">
      <ul>
          <li>
            <Link to="/home">Home</Link>
          </li>
          <li>
            <Link to="/user/goals">Goals</Link>
          </li>
        </ul>
        <Switch>
          <Route exact path="/">
            <Homepage />
          </Route>
          <Route path="/user/goals">
          <GoalList
            userGoals={enteredGoal}
          />
          <Route path="/user/new/goal"></Route>
          <CreateGoal onAddGoal={addGoalHandler} />
          </Route>
        </Switch>
        <ErrorBoundary key = {enteredGoal.id}>
        </ErrorBoundary>
      </div>
    </Router>
  );
}

export default App;
