import React, { useState }from "react";

const CreateGoal = props => {
  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredDescription, setEnteredDescription] = useState('');
  const [enteredDuration, setEnteredDuration] = useState('');

  const submitHandler = event => {
    event.preventDefault();
    props.onAddGoal({ title: enteredTitle, description: enteredDescription, duration: enteredDuration });
  };

  return (
    <div>
      <h1>Add a New Goal</h1>
      <form onSubmit={submitHandler}>
        <label>Title</label>
        <input type="text" value={enteredTitle} onChange={event => {
          setEnteredAmount(event.target.value);
        }}/>

        <label>Description</label>
        <input type="text" value={enteredDescription} />

        <label>Total Hours:</label>
        <input type="text" value={enteredDuration}/>

        <button type="submit">Create Goal</button>
      </form>
    </div>
  );
};

export default CreateGoal;
