import React, { useState }from "react";

const CreateGoal = props => {
  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredDescription, setEnteredDescription] = useState('');
  const [enteredDuration, setEnteredDuration] = useState('');

  const submitHandler = event => {
    event.preventDefault();
    props.onAddGoal({ title: enteredTitle, description: enteredDescription, duration: enteredDuration });
  };

  return (
    <div>
      <h1>Add a New Goal</h1>
      <form onSubmit={submitHandler}>
        <label>Title</label>
        <input name="title" type="text" value={enteredTitle} />

        <label>Description</label>
        <input name="description" type="text" ref={register} />

        <label>Total Hours:</label>
        <input name="hours" type="text" ref={register({ required: true })} />
        {errors.exampleRequired && <span>This field is required</span>}
        <button type="submit">Add</button>
      </form>
    </div>
  );
};

export default CreateGoal;
